class Pokegame extends Component {    
    constructor (props) {
        super(props);
        this.state = {
            loading: true,
            data1
        }
    }

    componentWillMount() {
        let data1 = this.fetchData()
            .then((res) => { this.setState({hand1: this.createHand(data1)})});
            let data2 = this.fetchData()
            .then((res) => { this.setState({hand2: this.createHand(data1)})});
    }

    render() {
        if (this.state.loading == false){
            return (
            <div className="Pokegame">
                <Pokedex hand={this.state.hand1} />
                <h1>VS</h1>
                <Pokedex hand={this.state.hand2} />
            </div>
        )
        } else {
            return <h1>Loading...</h1>
        }
    }

    createHand(data) {
        let hand = [];

        data.forEach(element => {
            hand = [
                hand.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
            ]
        });

        return hand;
    }

    fetchData() {
        let id = this.randomID();
        let url = `${NAME_API}${id}`
        let hand = [
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
            this.pareJSONData(id, url),
        ];

        return hand;
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }

    async pareJSONData (id, url) {
        let hand1 = [];
        let hand2 = [];


        const res = await fetch(url);
        const pokemon = await res.json();

        const pokemonName = pokemon.name;
        const pokemonEXP = pokemon.base_experience;
        const pokemonType = pokemon.types[0].type.name;
        //console.log(pokemon);
        //this.setState({data: pokemon});
        return pokemon;
        //this.setState({name: pokemonName, base_experience: pokemonEXP, type: pokemonType});
       //console.log('state changed');
    }
}

export default Pokegame;
