import React, { Component } from 'react';
import Pokedex from './Pokedex';
import Pokecard from './Pokecard';

const NAME_API =
    'https://pokeapi.co/api/v2/pokemon/';

class Pokegame extends Component {    
    constructor (props) {
        super(props);
        this.state = {
            loading: true,
            data: []
        }
    }

    componentWillMount() {
        this.buildHand();
    }

    render() {
        if (this.state.loading == false){
            let obj = this.buildHand();
            console.log(obj[0]);
            return (
                <div className="Pokegame">
                    <Pokedex hand={this.state.hand1}/>
                    <Pokedex hand={this.state.hand2}/>
                </div>
        )
        } else {
            return <h1>Loading...</h1>
        }
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }

    async fetchData() {
        let datas = [];

        for (step = 0; step < 8; step++) {
            let id = this.randomID();
            let url = `${NAME_API}${id}`;

            let res = await fetch(url);
            let json = await res.json();
            datas.push(json);
        }
        return datas;
    }

    async buildData() {
        let hand1 = [];
        let hand2 = [];
        let data1 = [];
        let data2 = [];

        let data = await this.fetchData();      
        return {data1, data2};

        //this.setState({ data1: data1, data2: data2});
    }

    async buildHand() {
        //console.log(data1);
        this.buildData()
            .then(function(result) {
                let obj = result;
                let data1 = obj.data1;
                let data2 = obj.data2;
                //console.log(data1);
                let hand1 = [];
                let hand2 = [];



                for(let step = 0; step < 8; step++) {
                    let element = data1[step];
                    console.log(data1);
                    hand1.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
                    let element2 = data2[step];
                    hand2.push({id: element2.id, name: element2.name, type: element2.types[0].type.name, base_experience: element2.base_experience})

                }
                this.setState({loading: false});
                return({hand1, hand2});
            })

    }


}

export default Pokegame;
