import React, { Component } from 'react';
import Pokedex from './Pokedex';
import Pokecard from './Pokecard';

const NAME_API =
    'https://pokeapi.co/api/v2/pokemon/';

class Pokegame extends Component {    
    constructor (props) {
        super(props);
        this.state = {
            loading: true,
            hand1: [],
            hand2: []
        }
    }

    componentWillMount() {
        this.buildHand();

    }

    render() {
        if (this.state.loading == false){
            return (
            <div className="Pokegame">
                <Pokedex hand={this.state.hand1} />
                <h1>VS</h1>
                <Pokedex hand={this.state.hand2} />
            </div>
        )
        } else {
            return <h1>Loading...</h1>
        }
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }

    async fetchData() {
        let id = this.randomID();
        let url = `${NAME_API}${id}`;
        let json = null;

        let response = await fetch(url);
        let data = await response.json();
        return data;
    }

    async buildHand() {
        let hand1 = [];
        let hand2 = [];
        let data1 = null;
        let data2 = null;

        for (let step = 0; step < 8; step++) {
            this.fetchData()
                .then(data => hand1.push(data));
        }

        for (let step = 0; step < 8; step++) {
            this.fetchData()
                .then(data => hand2.push(data));
        }

        return (
            <div className="Pokedex">
                <Pokedex hands={hand = hand1}/>

            </div>
        )
    }


    async parseJSONData () {
        let data1 = [];
        let data2 = [];

        let hand1 = [];
        let hand2 = [];

        for (let step = 0; step < 8; step++) {
            let id = this.randomID();
            let url = 
            fetch(url)
                .then(res => res.json())
                .then(json => data1.push(json));
        }

        for (let step = 0; step < 8; step++) {
            let id = this.randomID();
            let url = `${NAME_API}${id}`
            fetch(url)
                .then(res => res.json())
                .then(json => data2.push(json));

        }

        for (let step = 0; step < 8; step++) {
            console.log(data1[step]);
            let element = data1[step];
            hand1.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
        }

        console.log('here i am');
        data1.forEach(element => {
            console.log(element);
            hand1.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
        });
        data2.forEach(element => {
            hand2.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
        });

        this.setState({hand1: hand1, hand2: hand2, loading: false});
        console.log(hand1);
