import React, { Component } from 'react';
import Pokedex from './Pokedex';
import Pokecard from './Pokecard';

const NAME_API =
    'https://pokeapi.co/api/v2/pokemon/';

class Pokegame extends Component {    
    constructor (props) {
        super(props);
        this.state = {
            loading: true,
            hand1: [],
            hand2: []
        }
    }

    componentWillMount() {
        const data = this.buildData();
        this.buildHand(data[0]);
    }

    render() {
        if (this.state.loading == false){
            return (
                <div className="Pokegame">
                    <Pokedex hand={this.state.hand1}/>
                    <Pokedex hand={this.state.hand2}/>
                </div>
        )
        } else {
            return <h1>Loading...</h1>
        }
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }

    async fetchData() {
        let id = this.randomID();
        let url = `${NAME_API}${id}`;
        let json = null;

        let response = await fetch(url);
        let data = await response.json();
        return data;
    }

    async buildData() {
        let hand1 = [];
        let hand2 = [];
        let data1 = [];
        let data2 = [];

        for (let step = 0; step < 8; step++) {
            this.fetchData()
                .then(data => data1.push(data));
        }

        for (let step = 0; step < 8; step++) {
            this.fetchData()
                .then(data => data2.push(data));
        }


        return data1;



        //this.setState({ hand1: hand1, hand2: hand2});
    }

    buildHand(data) {
        console.log(data1);
        let hand1 = [];
        let hand2 = [];

        for(let step = 0; step < 8; step++) {
            let element = data1[step];
            hand1.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
            let element2 = data2[step];
            hand2.push({id: element2.id, name: element2.name, type: element2.types[0].type.name, base_experience: element2.base_experience})

        }
    }


}

export default Pokegame;
