import React, { Component } from 'react';
import './pokecard.css'; 


const POKE_API = 
    'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/';

const NAME_API =
    'https://pokeapi.co/api/v2/pokemon/';

class Pokecard extends Component {
    constructor (props) {
        super(props)
        this.state = {
            name: 'placeholder',
            base_experience: 0,
            type: 'basic'
        };

        this.parseDetails(this.props.id, `${NAME_API}${this.props.id}`);
    }

    render () {
        let imgSrc = `${POKE_API}${this.props.id}.png`;

        return (
        <div className="card">
            <h3>{this.state.name}</h3>
            <img src={imgSrc} alt={this.props.name}/>
            <p>type: {this.state.type}</p>
            <p>EXP: {this.state.base_experience}</p>
        </div>
        );

    }

    async parseDetails (id, url) {
        const res = await fetch(url);
        const pokemon = await res.json();

        const pokemonName = pokemon.name;
        const pokemonEXP = pokemon.base_experience;
        const pokemonType = pokemon.types[0].type.name;
        this.setState({name: pokemonName, base_experience: pokemonEXP, type: pokemonType});
       //console.log('state changed');
    }

}

export default Pokecard;
