import React, { Component } from 'react';
import Pokedex from './Pokedex';

const NAME_API =
    'https://pokeapi.co/api/v2/pokemon/';

class Pokegame extends Component {    
    constructor (props) {
        this.state = {
            data: null // default data that will be populated
        }
    }

    render() {
        return (
            <div className="Pokegame">
                <Pokedex hand={this.createHand()} />
                <h1>VS</h1>
                <Pokedex hand={this.createHand()} />
            </div>
        )
    }

    createHand() {
        let id = this.randomID();
        let url = `${NAME_API}${id}`
        let hand = [
            this.parseDetails(id, url),
            this.parseDetails(id, url),
            this.parseDetails(id, url),
            this.parseDetails(id, url),
            this.parseDetails(id, url),
            this.parseDetails(id, url),
            this.parseDetails(id, url),
            this.parseDetails(id, url),
        ];

        return hand;
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }

    async parseDetails (id, url) {
        const res = await fetch(url);
        const pokemon = await res.json();

        const pokemonName = pokemon.name;
        const pokemonEXP = pokemon.base_experience;
        const pokemonType = pokemon.types[0].type.name;
        console.log(pokemon);
        return pokemon;
        //this.setState({name: pokemonName, base_experience: pokemonEXP, type: pokemonType});
       //console.log('state changed');
    }
}

export default Pokegame;
