import React, { Component } from 'react';
import Pokedex from './Pokedex';

const NAME_API =
    'https://pokeapi.co/api/v2/pokemon/';

class Pokegame extends Component {    
    constructor (props) {
        super(props);
        this.state = {
            loading: true,
            data1
        }
    }

    componentWillMount() {
        let data1 = this.fetchData()
            .then((res) => { this.setState({hand1: this.createHand(data1)})});
            let data2 = this.fetchData()
            .then((res) => { this.setState({hand2: this.createHand(data1)})});
    }

    render() {
        if (this.state.loading == false){
            return (
            <div className="Pokegame">
                <Pokedex hand={this.state.hand1} />
                <h1>VS</h1>
                <Pokedex hand={this.state.hand2} />
            </div>
        )
        } else {
            return <h1>Loading...</h1>
        }
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }

    async pareJSONData () {
        let data1 = [];
        let data2 = [];

        let hand1 = [];
        let hand2 = [];

        for (let step = 0; step < 8; step++) {
            let id = this.randomID();
            let url = `${NAME_API}${id}`
            fetch(url)
                .then(res => res.json())
                .then(json => data1.push(json));
        }

        for (let step = 0; step < 8; step++) {
            let id = this.randomID();
            let url = `${NAME_API}${id}`
            fetch(url)
                .then(res => res.json())
                .then(json => data2.push(json));
        }

        data1.forEach(element => {
            hand1.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
        });
        data2.forEach(element => {
            hand2.push({id: element.id, name: element.name, type: element.types[0].type.name, base_experience: element.base_experience})
        });
        //const res = await fetch(url);
        //const pokemon = await res.json();
    }
}

export default Pokegame;
