import React, { Component } from 'react';
import Pokecard from './Pokecard';
import './pokedex.css';

class Pokedex extends Component {

    static defaultProps = {
        firstHand : [
            {id: this.randomID()},
            {id: this.randomID()},
            {id: this.randomID()},
            {id: this.randomID()},
            {id: this.randomID()},
            {id: this.randomID()},
            {id: this.randomID()},
            {id: this.randomID()}
        ]
    };
    render() {
        return(
            <div className="Pokedex">
                <h1>HAND ONE</h1>
                <div className="topHand">
                    {this.props.pokemon.map((p) => (
                        <Pokecard id={p.id}/>
                    ))}
                </div>
                <h1>HAND TWO</h1>

                <div className="bottomHand">
                    {this.props.pokemon2.map((p) => (
                        <Pokecard id={this.randomID()}/>
                    ))}
                </div>
            </div>
        )
    }

    randomID(){
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }
}

export default Pokedex;
