import React, { Component } from 'react';
import Pokecard from './Pokecard';
import './pokedex.css';

class Pokedex extends Component {
    constructor (props) {
        super(props)
        this.state = {
            ids: [this.randomID, this.randomID, this.randomID, this.randomID, this.randomID, this.randomID, this.randomID, this.randomID]
        };
    }

    render() {
        return(
            <div className="Pokedex">
                <h1>HAND ONE</h1>
                {this.createHand}
                <h1>HAND TWO</h1>

                <div className="bottomHand">
                    {this.state.ids.map((p) => (
                        <Pokecard id={this.randomID()}/>
                    ))}
                </div>
            </div>
        )
    }

    createHand(amount) {
        return(
            <div className="hand">
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
                <Pokecard id={this.randomID()}/>
            </div>
        )
    }

    randomID(){
        console.log('random');
        let min = Math.ceil(1);
        let max = Math.floor(150);
        return Math.floor(Math.random() * (max - min)) + min;
    }
}

export default Pokedex;
